type icsDBSchemaField string

func (f icsDBSchemaField) String() string {
	return string(f)
}

// IcsDBSchema stores db field names of Ics
var IcsDBSchema = struct {
	ID          icsDBSchemaField
	CreatedAt   icsDBSchemaField
	UpdatedAt   icsDBSchemaField
	Name        icsDBSchemaField
	Description icsDBSchemaField
	ICSURL      icsDBSchemaField
}{

	ID:          icsDBSchemaField("id"),
	CreatedAt:   icsDBSchemaField("created_at"),
	UpdatedAt:   icsDBSchemaField("updated_at"),
	Name:        icsDBSchemaField("name"),
	Description: icsDBSchemaField("description"),
	ICSURL:      icsDBSchemaField("ics_url"),
}

// Update updates Ics fields by primary key
func (o *Ics) Update(db *gorm.DB, fields ...icsDBSchemaField) error {
	dbNameToFieldName := map[string]interface{}{
		"id":          o.ID,
		"created_at":  o.CreatedAt,
		"updated_at":  o.UpdatedAt,
		"name":        o.Name,
		"description": o.Description,
		"ics_url":     o.ICSURL,
	}
	u := map[string]interface{}{}
	for _, f := range fields {
		fs := f.String()
		u[fs] = dbNameToFieldName[fs]
	}
	if err := db.Model(o).Updates(u).Error; err != nil {
		if err == gorm.ErrRecordNotFound {
			return err
		}

		return fmt.Errorf("can't update Ics %v fields %v: %s",
			o, fields, err)
	}

	return nil
}

// IcsUpdater is an Ics updates manager
type IcsUpdater struct {
	fields map[string]interface{}
	db     *gorm.DB
}

// NewIcsUpdater creates new Ics updater
func NewIcsUpdater(db *gorm.DB) IcsUpdater {
	return IcsUpdater{
		fields: map[string]interface{}{},
		db:     db.Model(&Ics{}),
	}
}

